mapper.insert:
INSERT
  INTO TBL_MENU
(
  MENU_NAME
, MENU_PRICE
, CATEGORY_CODE
, ORDERABLE_STATUS
)
VALUES
(
  #{ menuName }
, #{ menuPrice }
, #{ categoryCode }
, 'Y'
)

mapper.update:
UPDATE
       TBL_MENU
   SET MENU_NAME = #{ menuName }
     , MENU_PRICE = #{ menuPrice }
 WHERE MENU_CODE = #{ menuCode }

mapper.delete:
DELETE
  FROM TBL_MENU
 WHERE MENU_CODE = #{ menuCode }
